mapper.select:
  	SELECT * FROM
		(
		   SELECT A.*, ROWNUM RN
		   FROM (
		   select * from Administrator
		   
		   	
		   		name = #{administrator.username}
		   	
		   
		   
		   
		   ) A
		   
		)
	WHERE RN between #{pageNo} and #{pageNo}+#{pageSize}-1

SELECT * FROM ( SELECT A.*, ROWNUM RN FROM ( select * from Administrator
name like '%'||#{administrator.username}||'%'
) A
) WHERE RN between #{pageNo} and #{pageNo}+#{pageSize}-1

  	SELECT count(*) FROM
		(
		   SELECT A.*, ROWNUM RN
		   FROM (
		   select * from Administrator
		   
		   	
		   		name = #{administrator.username}
		   	
		   
		   
		   
		   ) A
		   
		)

SELECT count(*) FROM ( SELECT A.*, ROWNUM RN FROM ( select * from Administrator
name like '%'||#{administrator.username}||'%'
) A
)
